function OutData(inResultForm){show_notif();console.log(inResultForm)}function show_notif(){$('#modal-content-window-2').show(0).fadeTo("slow",1).delay(500).fadeTo("slow",0);setTimeout($('#modal-content-window-2').hide(0),1000)}function OrderSecurity(){$.ajax({type:"POST",url:"actions.php",data:'action=InstallRequest'+'&order_person_name='+$('#order-person-name').val()+'&order_person_tel='+$('#order-person-tel').val()+'&signaling='+$('#signaling').prop("checked")+'&alarm_button='+$('#alarm-button').prop("checked")+'&gas='+$('#gas').prop("checked")+'&fire='+$('#fire').prop("checked")+'&water='+$('#water').prop("checked")+'&service='+$('#service').prop("checked")+'&night='+$('night').prop("checked")+'&online_control='+$('#online_control').prop("checked")+'&price='+$('#price-block-price').text()+'&price_month='+$('#price-block-price-month').text(),success:function(data){OutData(data)}})}function FeedbackSend(){$.ajax({type:"POST",url:"actions.php",data:'action=Feedback'+'&feedback_name='+$('#feedback_name').val()+'&feedback_text='+$('#feedback_text').val(),success:function(data){OutData(data)}})}function Callme(){$.ajax({type:"POST",url:"actions.php",data:'action=Callme'+'&call_name='+$('#call_name').val()+'&call_tel='+$('#call_tel').val(),success:function(data){OutData(data)}})}function Modal_Callme(){$.ajax({type:"POST",url:"actions.php",data:'action=Callme'+'&call_name='+$('#modal_call_name').val()+'&call_tel='+$('#modal_call_tel').val(),success:function(data){OutData(data)}})}function SendQuestion(){$.ajax({type:"POST",url:"actions.php",data:'action=Question'+'&contact_name='+$('#contact_name').val()+'&contact_tel='+$('#contact_tel').val()+'&contact_text='+$('#contact_text').val(),success:function(data){OutData(data)}})}